import java.awt.Component;
import java.awt.Frame;
import java.net.MalformedURLException;
import java.net.URL;

import javax.swing.Icon;
import javax.swing.ImageIcon;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JOptionPane;

public class PhotoQuiz {

	public static void main(String[] args) throws Exception {
		Frame quizWindow = new Frame();
		quizWindow.setVisible(true);

		// 1. find an image on the internet, and put its URL in a String variable (from your browser, right click on the image, and select “Copy Image URL”)
		String imagee = "http://3.bp.blogspot.com/-aGRh0wedJsQ/TidxT6-BSQI/AAAAAAAADX0/0OsR1xzGJTY/s320/animais18.jpg";
			
			
		// 2. create a variable of type "Component" that will hold your image
		Component image1;
		// 3. use the "createImage()" method below to initialize your Component
		image1 = createImage(imagee);
		// 4. add the image to the quiz window
		quizWindow.add(image1);
		// 5. call the pack() method on the quiz window
		quizWindow.pack();
		// 6. ask a question that relates to the image
		String color = JOptionPane.showInputDialog("What color is the giraffe?");
		// 7. print "CORRECT" if the user gave the right answer
		if(color.equals("yellow")){
			JOptionPane.showMessageDialog(null, "CORRECT");
		}else{
			JOptionPane.showMessageDialog(null, "INCORRECT");
		}
		// 8. print "INCORRECT" if the answer is wrong

		// 9. remove the component from the quiz window (you may not see the effect of this until step 12)
		quizWindow.remove(image1);
		// 10. find another image and create it (might take more than one line of code)
		String imagee2 = "data:image/png;base64,";
		Component image2;
		image2 = createImage(imagee2);
		// 11. add the second image to the quiz window
		quizWindow.add(image2);
		// 12. pack the quiz window
		quizWindow.pack();
		// 13. ask another question
		int score = 0;
		String color2 = JOptionPane.showInputDialog("What color is rainbow dash?");
		if(color2.equals("rainbow")){
			JOptionPane.showMessageDialog(null, "CORRECT");
			JOptionPane.showMessageDialog(null, score);
			
	}else{
		JOptionPane.showMessageDialog(null, "INCORRECT");
	}
		

	}

	private static Component createImage(String imageUrl) throws MalformedURLException {
		URL url = new URL(imageUrl);
		Icon icon = new ImageIcon(url);
		JLabel imageLabel = new JLabel(icon);
		return imageLabel;
	}

	/* OPTIONAL */
	// *14. add scoring to your quiz
	// *15. make something happen when mouse enters image (imageComponent.addMouseMotionListener()) 
}




